import logo from './logo.svg';
import './App.css';
import React from 'react';
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.css'
class App extends React.Component {
  constructor() {
    super()
    this.state = {
      recipes: []
    }
    this.handleDelete=this.handleDelete.bind(this)
  }

  componentDidMount() {

    axios.get("http://localhost:3002/recipes")
      .then(response => {
        console.log(response.data)
        this.setState({ recipes: response.data })
      })
      .catch(error => {
        console.error('Error fetching recipes:', error);
      });
  }

  handleDelete(name){
    axios.delete(`http://localhost:3002/recipes/${name}`)
    this.setState({ recipes : this.state.recipes.filter(recipe=>recipe.name!=name) })
  }
  render() {
    return (
      <>

        <table className='table straped'>
          <thead>
            <tr>
              <td>NAME</td>
              <td>IMAGE</td>
            </tr>
          </thead>
          <tbody>
            {
              this.state.recipes.map((recipe) => (
                <tr key="recipe.name">
                  <td>{recipe.name}</td>
                  <td><img src={recipe.image} /></td>
                  <td>
                    <button onClick={() => this.handleDelete(recipe.name)}>delete</button>
                  </td>
                </tr>
              ))
            }
          </tbody>
        </table>




      </>
    )
  }
}

export default App;
